function back() {
    Motor.MotorPulse(12, 0)
    Motor.MotorPulse(13, 1000)
    Motor.MotorPulse(14, 0)
    Motor.MotorPulse(15, 1000)
}

function run() {
    Motor.MotorPulse(12, 1500)
    Motor.MotorPulse(13, 0)
    Motor.MotorPulse(14, 1500)
    Motor.MotorPulse(15, 0)
}

function right() {
    Motor.MotorPulse(12, 0)
    Motor.MotorPulse(13, 0)
    Motor.MotorPulse(14, 1500)
    Motor.MotorPulse(15, 0)
}

function left() {
    Motor.MotorPulse(12, 1500)
    Motor.MotorPulse(13, 0)
    Motor.MotorPulse(14, 0)
    Motor.MotorPulse(15, 0)
}

function brake() {
    Motor.MotorPulse(12, 0)
    Motor.MotorPulse(13, 0)
    Motor.MotorPulse(14, 0)
    Motor.MotorPulse(15, 0)
}

function spin_left() {
    Motor.MotorPulse(12, 2000)
    Motor.MotorPulse(13, 0)
    Motor.MotorPulse(14, 0)
    Motor.MotorPulse(15, 2000)
}

function spin_right() {
    Motor.MotorPulse(12, 0)
    Motor.MotorPulse(13, 2000)
    Motor.MotorPulse(14, 2000)
    Motor.MotorPulse(15, 0)
}

basic.forever(function on_forever() {
    if (input.buttonIsPressed(Button.A)) {
        run()
        basic.pause(1500)
        back()
        basic.pause(1500)
        left()
        basic.pause(1500)
        right()
        basic.pause(1500)
        spin_left()
        basic.pause(2000)
        spin_right()
        basic.pause(2000)
        brake()
    }

})
